import { useDispatch, useSelector } from "react-redux";
import CardsContainers from "../../components/Cardscontainer/CardsContainer";
import { useEffect, useState } from "react";
import { getUsers, getNameUser } from "../../redux/actions";
import NavBar from "../../components/Navbar/Nav.Bar";
import "./Home.css";

const Home = () => {
  const dispatch = useDispatch();
  const users = useSelector((state) => state.users);
  const loading = useSelector((state) => state.loading);
  const authenticated = useSelector((state) => state.isAuthenticated);
  const [input, setInput] = useState("");
  const [debouncedInput, setDebouncedInput] = useState(input);

  useEffect(() => {
    dispatch(getUsers());
  }, [dispatch]);

  useEffect(() => {
    const handler = setTimeout(() => {
      setDebouncedInput(input);
    }, 300);

    return () => {
      clearTimeout(handler);
    };
  }, [input]);

  useEffect(() => {
    if (debouncedInput) {
      dispatch(getNameUser(debouncedInput));
    } else {
      dispatch(getUsers());
    }
  }, [debouncedInput, dispatch]);

  const searchChange = (event) => {
    setInput(event.target.value);
  };

  return (
    <div className="container">
      {authenticated === true ? <NavBar /> : <></>}
      <nav class="navbar bg-body-tertiary ">
        <div class="container-fluid ">
          <form class="d-flex w-100" role="search">
            <input
              class="form-control me-2"
              type="search"
              placeholder="Search By Name User"
              aria-label="Search"
              value={input}
              onChange={searchChange}
            />
          </form>
        </div>
      </nav>
      {loading ? (
        <p>Loading...</p>
      ) : users.length === 0 ? (
        <p>No users found</p>
      ) : (
        <CardsContainers />
      )}
    </div>
  );
};

export default Home;
