import { Link } from "react-router-dom";
import style from "./NavBar.module.css";
import { useSelector } from "react-redux";
// importo los carchivos para poder hacer uso de Bootstrap
import "bootstrap/dist/css/bootstrap.min.css";
import "bootstrap/dist/js/bootstrap.bundle.min.js";

const NavBar = () => {
  const authenticated = useSelector((state) => state.isAuthenticated);
  const adminUser = useSelector((state) => state.profileUser);
  return (
    <>
      <div className={style.mainContainer}>
        {authenticated === true && adminUser.admin === true && (
          <>
            {/* <Link to="/Home">Home</Link>
            <Link to="/userProfile">UserProfile</Link> */}
            <button type="button" class="btn btn-primary btn-lg">
              Large button
            </button>
            <button type="button" class="btn btn-secondary btn-lg">
              Large button
            </button>
          </>
        )}
        {authenticated === false && (
          <>
            <Link to="/form">Sing in</Link>
            <Link to="/login">Login</Link>
          </>
        )}
      </div>
    </>
  );
};

export default NavBar;
