import axios from "axios";
import { useState } from "react";
import { useParams } from "react-router-dom";

const EditPost = () => {
  const { postId } = useParams();
  const [input, setInput] = useState({ id: "", comment: "" });

  const onChangeInputHandler = (e) => {
    const postComment = e.target.value;
    setInput(...input, (input.id = postId), (input.comment = postComment));
  };

  const onSubmitHandler = async (e) => {
    await axios.put(`http://localhost:3001/modifyPost`, input);
    e.preventDefault();
  };
  return (
    <div>
      <form onSubmit={onSubmitHandler}>
        <input type="text" value={input} onChange={onChangeInputHandler}>
          ingrese el posteo
        </input>
        <button>enviar</button>
      </form>
    </div>
  );
};

export default EditPost;
