import { useState } from "react";
import { Link } from "react-router-dom";
import "./Cards.css";

const Cards = (props) => {
  const [showMore, setShowMore] = useState(false); // Estado para controlar si se muestran más posts

  const handleShowMore = () => {
    setShowMore(!showMore);
  };

  return (
    <div className={`cardsContainer ${showMore ? "expanded" : ""}`}>
      <div className="cardLeft">
        <h1>Name: {props.name}</h1>
        <h2>LastName: {props.lastName}</h2>
        <div className={`cardPosts ${showMore ? "showScroll" : ""}`}>
          {props.Posts.map((post) => (
            <div key={post.id}>
              <p>Comment: {post.comment}</p>
            </div>
          ))}
        </div>
        {/* Mostrar botón si hay más de 3 posts */}
        {props.Posts.length > 3 && (
          <button className="showMoreButton" onClick={handleShowMore}>
            {showMore ? "Mostrar menos" : "Mostrar más"}
          </button>
        )}
      </div>
      <div className="cardRight">
        <img src={props.image} alt={`${props.name} ${props.lastName}`} />
        <Link to={`/detail/${props.id}`} className="cardRight2">
          <button>Detail</button>
        </Link>
      </div>
    </div>
  );
};

export default Cards;
