import { useState } from "react";
import { Link } from "react-router-dom";
import "./Cards.css";

const Cards = (props) => {
  const [showMore, setShowMore] = useState(false); // Estado para controlar si se muestran m치s posts
  const visiblePosts = showMore ? props.Posts : props.Posts.slice(0, 3); // Mostrar 3 o todos

  const handleShowMore = () => {
    setShowMore(!showMore);
  };

  return (
    <div className="cardsContainer">
      <div className="cardLeft">
        <h1>Name: {props.name}</h1>
        <h2>LastName: {props.lastName}</h2>
        <div className="cardPosts">
          {visiblePosts.map((post) => (
            <div key={post.id}>
              <p>Comment: {post.comment}</p>
            </div>
          ))}
          {props.Posts.length > 3 && (
            <button className="showMoreButton" onClick={handleShowMore}>
              {showMore ? "Mostrar menos" : "Mostrar m치s"}
            </button>
          )}
        </div>
      </div>
      <div className="cardRight">
        <img src={props.image} alt={`${props.name} ${props.lastName}`} />
        <Link to={`/detail/${props.id}`} className="cardRight2">
          <button>Detail</button>
        </Link>
      </div>
    </div>
  );
};

export default Cards;
