import { useNavigate, useParams } from "react-router-dom";
import axios from "axios";
import { useEffect, useState } from "react";
import "./ModifyUser.css";
import { refreshProfileUser } from "../../redux/actions";
import { useDispatch } from "react-redux";

const Detail = () => {
  const { id } = useParams(); // Obtener el ID del usuario de los parámetros de la URL
  const [user, setUser] = useState({}); // Estado para almacenar los detalles del usuario
  const dispatch = useDispatch();
  const navigate = useNavigate();

  // Efecto para obtener los detalles del usuario al cargar el componente
  useEffect(() => {
    const getDetailUser = async () => {
      try {
        const { data } = await axios.get(`http://localhost:3001/users/${id}`);
        setUser(data); // Establecer el estado del usuario con los datos obtenidos
      } catch (error) {
        console.error("Error al obtener los detalles del usuario:", error);
        alert("Error al obtener los detalles del usuario");
      }
    };
    getDetailUser();
  }, [id]); // Agregar 'id' como dependencia para volver a ejecutar si cambia

  // Función para eliminar el usuario
  const deleteHandler = async () => {
    const deleteUser = window.confirm(
      "¿Estás seguro que quieres eliminar tu cuenta de usuario?"
    );
    if (deleteUser) {
      try {
        await axios.delete(`http://localhost:3001/users/deleteUser/${id}`);
        alert("Usuario eliminado");
        navigate("/login");
      } catch (error) {
        console.error("Error al eliminar el usuario:", error);
        alert("Error al eliminar el usuario");
      }
    }
  };

  // Función para volver al perfil del usuario
  const onClickBackHandler = () => {
    navigate("/userProfile");
  };

  // Función para manejar cambios en los inputs
  const onChangeHandler = (event) => {
    const { name, value } = event.target;
    setUser((prevUser) => ({ ...prevUser, [name]: value })); // Actualizar el estado del usuario
  };

  // Función para enviar los datos del formulario
  const submitHandler = async (event) => {
    event.preventDefault();
    try {
      await axios.put(`http://localhost:3001/users/modifyUser/${id}`, user);
      alert("Usuario modificado correctamente");
      dispatch(refreshProfileUser(id)); // Refrescar el perfil del usuario
      navigate("/userProfile"); // Navegar de regreso al perfil
    } catch (error) {
      console.error(
        "Error al modificar el usuario:",
        error.response?.data || error.message
      );
      alert("Error al modificar el usuario");
    }
  };

  // Función para subir una imagen a Cloudinary
  const onImageUploadHandler = async (event) => {
    const file = event.target.files[0];
    const formData = new FormData();
    formData.append("file", file);
    formData.append("upload_preset", "ucyfuf2e");

    try {
      const { data } = await axios.post(
        "https://api.cloudinary.com/v1_1/dekk238ao/image/upload",
        formData
      );
      setUser((prevUser) => ({ ...prevUser, image: data.secure_url })); // Actualizar el estado del usuario con la nueva imagen
    } catch (error) {
      console.error("Error al subir la imagen:", error);
      alert("Error al subir la imagen");
    }
  };

  return (
    <div className="container">
      <form onSubmit={submitHandler}>
        <div className="mainContainer">
          <label>Name: </label>
          <input
            type="text"
            value={user.name || ""}
            onChange={onChangeHandler}
            name="name"
            required
          />

          <label>LastName: </label>
          <input
            type="text"
            value={user.lastName || ""}
            onChange={onChangeHandler}
            name="lastName"
            required
          />

          <label>Image: </label>
          <input type="file" onChange={onImageUploadHandler} name="image" />
          {user.image && (
            <img src={user.image} alt="Imagen subida" width="100" />
          )}

          <label>Email: </label>
          <input
            type="text"
            value={user.email || ""}
            onChange={onChangeHandler}
            name="email"
            required
          />

          <label>Phone: </label>
          <input
            type="text"
            value={user.phone || ""}
            onChange={onChangeHandler}
            name="phone"
            required
          />

          <label>NameUser: </label>
          <input
            type="text"
            value={user.nameUser || ""}
            onChange={onChangeHandler}
            name="nameUser"
            required
          />

          <label>Password: </label>
          <input
            type="password"
            value={user.password || ""}
            onChange={onChangeHandler}
            name="password"
            required
          />

          <button type="submit">Enviar</button>
        </div>
      </form>
      <div className="buttonModifyUser">
        <button onClick={deleteHandler} className="deleteUser">
          Delete User
        </button>
        <button onClick={onClickBackHandler} className="backButton">
          Back
        </button>
      </div>
    </div>
  );
};

export default Detail;
