import { useState, useEffect } from "react";
import style from "./Form.module.css";
import axios from "axios";
import { GoogleLogin } from "react-google-login";
import { gapi } from "gapi-script";
import { useNavigate } from "react-router-dom"; // Importar useNavigate
import { getUserProfile } from "../../redux/actions";
import { useDispatch } from "react-redux";

const Form = () => {
  const navigate = useNavigate(); // Inicializar useNavigate
  const dispatch = useDispatch();

  const [form, setForm] = useState({
    name: "",
    lastName: "",
    image: "",
    email: "",
    phone: "",
    nameUser: "",
    password: "",
  });

  const [errors, setErrors] = useState({
    email: "",
  });

  // Inicializar gapi autenticacion con google
  useEffect(() => {
    const initClient = () => {
      gapi.client.init({
        clientId:
          "133661170359-fkebjnpt8sudfqujbcjbjrtj1cc8veoc.apps.googleusercontent.com",
      });
    };
    gapi.load("client:auth2", initClient);
  }, []);

  // Validación de correo electrónico
  const validate = (form) => {
    if (/^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,4})+$/.test(form.email)) {
      setErrors({ ...errors, email: "" });
    } else {
      setErrors({ ...errors, email: "Email incorrecto" });
    }
    if (form.email === "") setErrors({ ...errors, email: "Campo vacío" });
  };
  // Manejar cambios en los inputs
  const onChangeHandler = (event) => {
    const property = event.target.name;
    const value = event.target.value;
    validate({ ...form, [property]: value });
    setForm({ ...form, [property]: value });
  };
  // Subir imagen a Cloudinary
  const onImageUploadHandler = async (event) => {
    const file = event.target.files[0];
    const formData = new FormData();
    formData.append("file", file);
    formData.append("upload_preset", "ucyfuf2e");

    try {
      const response = await axios.post(
        "https://api.cloudinary.com/v1_1/dekk238ao/image/upload",
        formData
      );
      setForm({ ...form, image: response.data.secure_url });
    } catch (error) {
      console.error("Error al subir la imagen:", error);
      alert("Error al subir la imagen");
    }
  };

  //genera contraseña aleatoria
  const generateRandomString = (length) => {
    const characters =
      "ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789";
    let result = "";
    for (let i = 0; i < length; i++) {
      result += characters.charAt(
        Math.floor(Math.random() * characters.length)
      );
    }
    return result;
  };

  // Manejar envío del formulario
  const submitHandler = (event) => {
    event.preventDefault();
    if (!errors.email) {
      try {
        axios
          .post("http://localhost:3001/users/createUser", form)
          .then((res) => alert("Usuario creado"))
          .catch((err) => {
            const errorMessage =
              err.response?.data?.error || "Error en la creación del usuario";
            alert(errorMessage);
          }); // Muestra el mensaje en la alerta
      } catch (error) {
        alert(
          "Error al crear el usuario:",
          error.response?.data || error.message
        );
      }
    }
  };

  const onSuccessGoogle = async (response) => {
    const userInfo = response.profileObj; // Información del perfil de Google

    // Generar valores aleatorios
    const randomPhone = `+1${
      Math.floor(Math.random() * 9000000000) + 1000000000
    }`; // Generar número de teléfono aleatorio
    const randomNameUser = `${userInfo.givenName}${
      userInfo.familyName
    }${Math.floor(Math.random() * 1000)}`; // Generar nombre de usuario aleatorio
    const randomPassword = generateRandomString(12); // Generar contraseña aleatoria de 12 caracteres

    setForm({
      ...form,
      name: userInfo.givenName,
      lastName: userInfo.familyName,
      email: userInfo.email,
      image: userInfo.imageUrl,
      phone: randomPhone,
      nameUser: randomNameUser,
      password: randomPassword,
    });

    // Enviar los datos al servidor
    try {
      const res = await axios.post("http://localhost:3001/users/createUser", {
        name: userInfo.givenName,
        lastName: userInfo.familyName,
        email: userInfo.email,
        image: userInfo.imageUrl,
        phone: randomPhone,
        nameUser: randomNameUser,
        password: randomPassword,
      });

      // Redirigir al perfil del usuario
      alert("Usuario creado con Google: " + res.data.message);
      const authenticated = await dispatch(
        getUserProfile({ nameUser: randomNameUser, password: randomPassword })
      );
      if (authenticated) {
        navigate("/userProfile");
      }
    } catch (error) {
      alert("Usuario ya existente, por favor modifique los datos");
    }
  };

  return (
    <>
      <div>
        <h2>Weekly Coding Challenge #1: Sign in/up Form</h2>
        <div class="container" id="container">
          <div class="form-container sign-up-container">
            <form action="#">
              <h1>Create Account</h1>
              <div class="social-container">
                <a href="#" class="social">
                  <i class="fab fa-facebook-f"></i>
                </a>
                <a href="#" class="social">
                  <i class="fab fa-google-plus-g"></i>
                </a>
                <a href="#" class="social">
                  <i class="fab fa-linkedin-in"></i>
                </a>
              </div>
              <span>or use your email for registration</span>
              <input type="text" placeholder="Name" />
              <input type="email" placeholder="Email" />
              <input type="password" placeholder="Password" />
              <button>Sign Up</button>
            </form>
          </div>
          <div class="form-container sign-in-container">
            <form action="#">
              <h1>Sign in</h1>
              <div class="social-container">
                <a href="#" class="social">
                  <i class="fab fa-facebook-f"></i>
                </a>
                <a href="#" class="social">
                  <i class="fab fa-google-plus-g"></i>
                </a>
                <a href="#" class="social">
                  <i class="fab fa-linkedin-in"></i>
                </a>
              </div>
              <span>or use your account</span>
              <input type="email" placeholder="Email" />
              <input type="password" placeholder="Password" />
              <a href="#">Forgot your password?</a>
              <button>Sign In</button>
            </form>
          </div>
          <div class="overlay-container">
            <div class="overlay">
              <div class="overlay-panel overlay-left">
                <h1>Welcome Back!</h1>
                <p>
                  To keep connected with us please login with your personal info
                </p>
                <button class="ghost" id="signIn">
                  Sign In
                </button>
              </div>
              <div class="overlay-panel overlay-right">
                <h1>Hello, Friend!</h1>
                <p>Enter your personal details and start journey with us</p>
                <button class="ghost" id="signUp">
                  Sign Up
                </button>
              </div>
            </div>
          </div>
        </div>

        <footer>
          <p>
            Created with <i class="fa fa-heart"></i> by
            <a target="_blank" href="https://florin-pop.com">
              Florin Pop
            </a>
            - Read how I created this and how you can join the challenge
            <a
              target="_blank"
              href="https://www.florin-pop.com/blog/2019/03/double-slider-sign-in-up-form/"
            >
              here
            </a>
            .
          </p>
        </footer>
      </div>
      <div>
        <form onSubmit={submitHandler}>
          <div className={style.mainContainer}>
            <label>Name: </label>
            <input
              type="text"
              value={form.name}
              onChange={onChangeHandler}
              name="name"
            />
            <label>LastName: </label>
            <input
              type="text"
              value={form.lastName}
              onChange={onChangeHandler}
              name="lastName"
            />
            <label>Image: </label>
            <input type="file" onChange={onImageUploadHandler} name="image" />
            {form.image && (
              <img src={form.image} alt="Imagen subida" width="100" />
            )}
            <label>Email: </label>
            <input
              type="text"
              value={form.email}
              onChange={onChangeHandler}
              name="email"
            />
            {errors.email && <span>{errors.email}</span>}
            <label>Phone: </label>
            <input
              type="text"
              value={form.phone}
              onChange={onChangeHandler}
              name="phone"
            />
            <label>NameUser: </label>
            <input
              type="text"
              value={form.nameUser}
              onChange={onChangeHandler}
              name="nameUser"
            />
            <label>Password: </label>
            <input
              type="password"
              value={form.password}
              onChange={onChangeHandler}
              name="password"
            />
            <button type="submit">Enviar</button>
          </div>
        </form>

        <div className={style.oauthContainer}>
          <p>O inicia sesión con Google:</p>
          <GoogleLogin
            clientId="133661170359-fkebjnpt8sudfqujbcjbjrtj1cc8veoc.apps.googleusercontent.com"
            buttonText="Login with Google"
            onSuccess={onSuccessGoogle}
            cookiePolicy={"single_host_origin"}
          />
        </div>
      </div>
    </>
  );
};

export default Form;
