import { useState, useCallback } from "react";
import { Link } from "react-router-dom";
import "./Cards.css";

const Cards = ({ name, lastName, image, id, Posts }) => {
  // Estado local para manejar si se muestran más o menos posts
  const [showMore, setShowMore] = useState(false);

  // Selecciona qué posts mostrar (3 primeros o todos si se muestra más)
  const visiblePosts = showMore ? Posts : Posts.slice(0, 3);

  // Maneja el cambio entre mostrar más o menos posts
  const handleShowMore = useCallback(() => {
    setShowMore((prevShowMore) => !prevShowMore);
  }, []);

  return (
    <div className="cardsContainer">
      {/* Contenido principal del usuario */}
      <div className="cardLeft">
        <h1>
          {name} {lastName}
        </h1>

        {/* Lista de posts visibles */}
        <div className="cardPosts">
          {visiblePosts.map((post) => (
            <div key={post.id}>
              <p>{post.comment}</p>
            </div>
          ))}

          {/* Botón para mostrar más/menos posts */}
          {Posts.length > 3 && (
            <button className="showMoreButton" onClick={handleShowMore}>
              {showMore ? "Mostrar menos" : "Mostrar más"}
            </button>
          )}
        </div>
      </div>

      {/* Imagen y botón de detalle */}
      <div className="cardRight">
        <img src={image} alt={`${name} ${lastName}`} />

        {/* Enlace al detalle del usuario */}
        <Link to={`/detail/${id}`} className="cardRight2">
          <button>Detail</button>
        </Link>
      </div>
    </div>
  );
};

export default Cards;
