import { useDispatch, useSelector } from "react-redux";
import { useNavigate } from "react-router-dom";
import { logOut, refreshProfileUser } from "../../redux/actions";
import { useEffect, useState, useCallback } from "react";
import axios from "axios"; // Para obtener todos los posts si es admin
import "./userProfile.css";

const UserProfile = () => {
  const profile = useSelector((state) => state.profileUser); // Obtiene el perfil del estado
  const authenticated = useSelector((state) => state.isAuthenticated); // Verifica si el usuario está autenticado
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const [showMore, setShowMore] = useState(false); // Estado para manejar la expansión de publicaciones
  const [posts, setPosts] = useState([]); // Estado para los posts
  const [isAdmin, setIsAdmin] = useState(false); // Estado para verificar si el usuario es admin

  // Redirigir al login si no está autenticado
  useEffect(() => {
    if (!authenticated) {
      navigate("/login"); // Redirigir a login si no está autenticado
    } else if (profile && profile.id) {
      dispatch(refreshProfileUser(profile.id)); // Llama a la acción para obtener el perfil del usuario

      // Verificar si el usuario es admin
      if (profile.admin) {
        setIsAdmin(true);
        // Obtener todos los posts si es admin
        axios
          .get("http://localhost:3001/postUser")
          .then((response) => {
            setPosts(response.data); // Guardar todos los posts
          })
          .catch((error) => {
            console.error("Error al obtener los posts:", error); // Manejo de errores
          });
      } else {
        setPosts(profile.Posts); // Mostrar solo los posts del usuario si no es admin
      }
    }
  }, [authenticated, navigate, profile, dispatch]);

  // Función para manejar la navegación a la página de inicio de sesión
  const onClickSingInHandler = () => {
    navigate("/form");
  };

  // Función para manejar la navegación a la página de inicio de sesión
  const onClickLoginHandler = () => {
    navigate("/login");
  };

  // Función para manejar el cierre de sesión
  const onClickLogOutHandler = useCallback(() => {
    const closeSesion = window.confirm("¿Cerrar Sesión?");
    if (closeSesion) {
      dispatch(logOut());
      navigate("/login");
    }
  }, [dispatch, navigate]);

  // Función para manejar la modificación del perfil del usuario
  const onClickModifyUserHandler = () => {
    navigate(`/modifyUser/${profile.id}`);
  };

  // Función para manejar la visualización de todos los posts
  const allPostsHandler = () => {
    navigate(`/posts/${profile.id}`);
  };

  // Manejar la visualización de más publicaciones
  const handleShowMore = () => {
    setShowMore((prevState) => !prevState); // Cambia el estado de mostrar más o menos publicaciones
  };

  return (
    <div className="container">
      {authenticated ? (
        profile ? (
          <div className="dateUser">
            <div className="dateUserHead">
              <div className="dateUserLeft">
                <h1>
                  {profile.name} {profile.lastName}
                </h1>
                <h3>All Posts User</h3>
              </div>
              <div className="dateUserRight">
                <img src={profile.image} alt="Profile" />{" "}
                {/* Imagen del perfil */}
              </div>
            </div>

            <div className="dateUserButton">
              <ul>
                {posts.length > 0 ? (
                  posts.slice(0, showMore ? posts.length : 3).map((post) => (
                    <div className="dateUserButtonItems" key={post.id}>
                      <li>
                        <p>{post.comment}</p>
                      </li>
                    </div>
                  ))
                ) : (
                  <p>No hay posts disponibles.</p>
                )}
              </ul>
              {posts.length > 3 && (
                <div className="showMoreButton">
                  <button className="buttonNew" onClick={handleShowMore}>
                    {showMore ? "Mostrar menos" : "Mostrar más"}
                  </button>
                </div>
              )}
            </div>

            <div className="dateUserFoot">
              <button onClick={allPostsHandler}>All Posts</button>
              <button onClick={onClickModifyUserHandler}>Edit Profile</button>
              <button onClick={onClickLogOutHandler} className="backButton">
                Log Out
              </button>
            </div>
          </div>
        ) : (
          <p>Cargando perfil...</p>
        )
      ) : (
        <>
          <p>
            No estás autenticado. Por favor, inicia sesión para ver el perfil.
          </p>
          <button onClick={onClickSingInHandler}>Sing In</button>
          <button onClick={onClickLoginHandler}>Login</button>
        </>
      )}
    </div>
  );
};

export default UserProfile;
