import { useState, useEffect } from "react";
import { useDispatch } from "react-redux";
import { useNavigate } from "react-router-dom";
import { GoogleLogin } from "react-google-login";
import { gapi } from "gapi-script";
import { getUserProfile } from "../../redux/actions";
import style from "./Login.module.css";

const Login = () => {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const [login, setLogin] = useState({ nameUser: "", password: "" });

  useEffect(() => {
    const initClient = () => {
      if (!gapi.auth2.getAuthInstance()) {
        // Chequear si ya está inicializado
        gapi.client.init({
          clientId:
            "133661170359-fkebjnpt8sudfqujbcjbjrtj1cc8veoc.apps.googleusercontent.com",
          scope: "email",
        });
      }
    };

    gapi.load("client:auth2", initClient); // Solo cargar `gapi` una vez
  }, []); // Asegurarse que el hook solo se ejecute una vez con el array vacío

  const onChangeHandler = (event) => {
    const property = event.target.name;
    const value = event.target.value;
    setLogin({ ...login, [property]: value });
  };

  const onSubmitHandler = async (event) => {
    event.preventDefault();
    try {
      const authenticated = await dispatch(getUserProfile(login));
      if (authenticated) {
        navigate("/userProfile");
      }
    } catch (error) {
      alert(error);
    }
  };

  const onSuccessGoogle = async (response) => {
    try {
      const userInfo = response.profileObj; // Obtener la información del usuario desde Google
      console.log("Google user info: ", userInfo);

      // Despachar la acción para obtener el perfil del usuario
      await dispatch(getUserProfile(userInfo));

      // Redirigir al perfil del usuario
      navigate("/userProfile");
    } catch (error) {
      console.error("Error al obtener el perfil del usuario:", error);
    }
  };

  const onFailureGoogle = (response) => {
    console.error("Error en la autenticación con Google:", response);
    alert("Error en la autenticación con Google");
  };

  return (
    <div>
      <form onSubmit={onSubmitHandler}>
        <div className={style.mainContainer}>
          <label>User</label>
          <input
            type="text"
            value={login.nameUser}
            name="nameUser"
            onChange={onChangeHandler}
          />
          <label>Password</label>
          <input
            type="password"
            value={login.password}
            name="password"
            onChange={onChangeHandler}
          />
          <button type="submit">Login</button>
        </div>
      </form>

      <div className={style.oauthContainer}>
        <p>Or login with:</p>
        <GoogleLogin
          clientId="133661170359-fkebjnpt8sudfqujbcjbjrtj1cc8veoc.apps.googleusercontent.com"
          buttonText="Login with Google"
          onSuccess={onSuccessGoogle}
          onFailure={onFailureGoogle}
          cookiePolicy={"single_host_origin"}
        />
      </div>
    </div>
  );
};

export default Login;
