import { Link, useLocation } from "react-router-dom";
import { useSelector } from "react-redux";
import Home from "../../views/Home/Home";
import UserProfile from "../userProfile/userProfile";
import "bootstrap/dist/css/bootstrap.min.css";
import "bootstrap/dist/js/bootstrap.bundle.min.js";

const NavBar = () => {
  const authenticated = useSelector((state) => state.isAuthenticated);
  const adminUser = useSelector((state) => state.profileUser);
  const location = useLocation();

  return (
    <div id="containerNavBar" className="container-fluid">
      {authenticated === true && adminUser.admin === true && (
        <div className="card text-center">
          <div className="card-header">
            <ul className="nav nav-tabs card-header-tabs">
              <li className="nav-item">
                {/* Remover el <a> y dejar solo <Link> */}
                <Link className="nav-link" to="/home">
                  Home
                </Link>
              </li>
              <li className="nav-item">
                <Link className="nav-link" to="/userProfile">
                  UserProfile
                </Link>
              </li>
            </ul>
          </div>
          {/* <div className="card-body">
            {/* Comparar correctamente el path y mantener consistencia en mayúsculas/minúsculas */}
          {/* {location.pathname === "/home" ? <Home /> : <UserProfile />} */}
        </div>
      )}
    </div>
  );
};

export default NavBar;
