import Cards from "../Cards/Cards";
import { useSelector } from "react-redux";
import style from "./CardsContainer.module.css";
import { Link } from "react-router-dom";
import { useEffect, useState } from "react";
import axios from "axios";

const CardsContainers = () => {
  const users = useSelector((state) => state.users);
  const [user, setUser] = useState("");
  //mapear cada usuario y por cada usuario ejecutar una funcion que pegue por id a la url que trae
  //los usuarios por id y es informacion guardarla en un estado local

  const dateUsers = async (id) => {
    const date = await axios.get(`http://localhost:3001/users/${id}`);
    setUser(date.data);
  };
  console.log(user);

  return (
    <>
      <div className={style.mainContainer}>
        {users.map((users) => {
          dateUsers(users.id);
          return (
            <>
              {users.admin === false &&
                user.length > 0 &&
                user.map((elem) => {
                  return (
                    <>
                      <Cards
                        id={elem.id}
                        name={elem.name}
                        lastName={elem.lastName}
                        image={elem.image}
                        email={elem.email}
                        phone={elem.phone}
                      />
                    </>
                  );
                })}
            </>
          );
        })}
      </div>
    </>
  );
};

export default CardsContainers;
