import { useNavigate, useParams } from "react-router-dom";
import axios from "axios";
import { useEffect, useState } from "react";
import { refreshProfileUser, getUsers } from "../../redux/actions";
import { useDispatch } from "react-redux";
import "./Detail.css";

const Detail = () => {
  const { id } = useParams(); // Extrae el ID del usuario de la URL
  const [user, setUser] = useState(null); // Estado para almacenar los datos del usuario
  const navigate = useNavigate(); // Hook para la navegación programática
  const dispatch = useDispatch(); // Hook para despachar acciones de Redux

  // useEffect que carga los detalles del usuario al montar el componente
  useEffect(() => {
    const getDetailUser = async () => {
      try {
        const detailUser = await axios.get(`http://localhost:3001/users/${id}`);
        setUser(detailUser.data); // Establece el estado del usuario con los datos obtenidos
      } catch (error) {
        console.error("Error al obtener los detalles del usuario:", error);
        alert("Error al obtener los detalles del usuario.");
      }
    };
    getDetailUser();
  }, [id]);

  // Función para manejar la eliminación del usuario
  const deleteHandlerUser = async () => {
    const deleteUser = window.confirm(
      "¿Estás seguro de que deseas eliminar este usuario?"
    );
    if (deleteUser) {
      try {
        await axios.delete(`http://localhost:3001/users/deleteUser/${id}`);
        alert("Usuario eliminado");

        dispatch(getUsers()); // Actualiza la lista de usuarios en Redux
        navigate("/home"); // Redirige al home
      } catch (error) {
        alert("Error al eliminar el usuario: " + error.message);
      }
    }
  };

  // Función para refrescar el perfil del usuario
  const refreshProfile = async () => {
    try {
      await dispatch(refreshProfileUser(id)); // Despacha la acción para recargar el perfil del usuario
    } catch (error) {
      console.error("Error al recargar el perfil:", error);
    }
  };

  // Función para eliminar un post específico
  const onclickDeletePostHandler = async (postId) => {
    const confirmDelete = window.confirm(
      "¿Estás seguro de que deseas eliminar este post?"
    );
    if (confirmDelete) {
      try {
        await axios.delete(
          `http://localhost:3001/postUser/deletePost/${postId}`
        );
        alert("Post eliminado correctamente");

        // Filtra el post eliminado del estado local
        const updatedPosts = user.Posts.filter((post) => post.id !== postId);
        setUser({ ...user, Posts: updatedPosts }); // Actualiza los posts en el estado
        dispatch(getUsers()); // Actualiza la lista de usuarios
        navigate("/home"); // Redirige al home
      } catch (error) {
        alert("Error al eliminar el post: " + error.message);
      }
    }
  };

  // Función para manejar la navegación de vuelta a la vista Home
  const onClickBackHandler = () => {
    navigate("/home");
  };

  return (
    <div className="container">
      {user ? (
        <>
          <div className="headContainer">
            <h1>
              {user.name} {user.lastName}
            </h1>
            <img src={user.image} alt={`${user.name} ${user.lastName}`} />
          </div>

          <div className="dateUser">
            <p>
              <strong>Email: </strong>
              {user.email}
            </p>
            <p>
              <strong>Teléfono: </strong>
              {user.phone}
            </p>
            <p>
              <strong>Usuario:</strong> {user.nameUser}
            </p>
            <p>
              <strong>Contraseña:</strong> {user.password}
            </p>
          </div>

          {/* Renderiza los posts del usuario */}
          <div className="postsUser">
            {user.Posts && user.Posts.length > 0 ? (
              user.Posts.map((post) => (
                <div key={post.id} className="cardsContainer">
                  <p>{post.comment}</p>
                  <button
                    onClick={() => onclickDeletePostHandler(post.id)}
                    className="deletePost"
                  >
                    Delete post
                  </button>
                </div>
              ))
            ) : (
              <p>No posts available.</p>
            )}
          </div>
        </>
      ) : (
        <p>Cargando detalles del usuario...</p> // Mensaje de carga
      )}

      <div className="buttonModifyUser">
        <button onClick={deleteHandlerUser} className="deleteUser">
          Delete User
        </button>

        <button onClick={onClickBackHandler} className="backButton">
          Back
        </button>
      </div>
    </div>
  );
};

export default Detail;
