import { useNavigate, useParams } from "react-router-dom";
import axios from "axios";
import { useEffect, useState } from "react";
import { refreshProfileUser, getUsers } from "../../redux/actions";
import { useDispatch } from "react-redux";
import "./Detail.css";

const Detail = () => {
  const { id } = useParams();
  const [user, setUser] = useState("");
  const navigate = useNavigate();
  const dispatch = useDispatch();

  useEffect(() => {
    const getDetailUser = async () => {
      const detailUser = await axios.get(`http://localhost:3001/users/${id}`);
      setUser(detailUser.data);
    };
    getDetailUser();
  }, [id]);

  const deleteHandler = async () => {
    const deleteUser = window.confirm(
      "¿Estás seguro de que deseas eliminar este usuario?"
    );
    if (deleteUser) {
      try {
        await axios.delete(`http://localhost:3001/users/deleteUser/${id}`);
        alert("Usuario eliminado");

        // Actualizamos la lista de usuarios en Redux
        dispatch(getUsers());

        // Redirigimos al home
        navigate("/home");
      } catch (error) {
        alert("Error al eliminar el usuario: " + error.message);
      }
    }
  };

  const refreshProfile = async () => {
    try {
      await dispatch(refreshProfileUser(id)); // Recargar el perfil actualizado
    } catch (error) {
      console.error("Error al recargar el perfil:", error);
    }
  };

  const onclickDeleteHandler = async (postId) => {
    const confirmDelete = window.confirm(
      "¿Estás seguro de que deseas eliminar este post?"
    );

    if (confirmDelete) {
      try {
        await axios.delete(
          `http://localhost:3001/postUser/deletePost/${postId}`
        );
        alert("Post eliminado correctamente");

        // Aquí filtramos el post eliminado del estado local
        const updatedPosts = user.Posts.filter((post) => post.id !== postId);
        setUser({ ...user, Posts: updatedPosts });
        refreshProfile();
      } catch (error) {
        alert("Error al eliminar el post: " + error.message);
      }
    }
  };

  const onClickBackHandler = () => {
    navigate("/home");
  };

  return (
    <div className="container">
      {user && (
        <>
          <div className="headContainer">
            <h1>
              {user.name} {user.lastName}
            </h1>
            <img src={`${user.image}`} alt={`${user.name} ${user.lastName}`} />
          </div>
          <p>Email: {user.email}</p>
          <p>Teléfono: {user.phone}</p>
          <p>Usuario: {user.nameUser}</p>
          <p>Contraseña: {user.password}</p>
          <div className="postsUser">
            {user.Posts && user.Posts.length > 0 ? (
              user.Posts.map((post) => (
                <li key={post.id}>
                  <p>Comentario: {post.comment}</p>
                  <button
                    onClick={() => onclickDeleteHandler(post.id)}
                    className="deletePost"
                  >
                    Eliminar post
                  </button>
                </li>
              ))
            ) : (
              <p>No hay posts disponibles.</p>
            )}
          </div>
        </>
      )}
      <div className="buttonModifyUser">
        <button onClick={deleteHandler} className="deleteUser">
          Eliminar Usuario
        </button>

        <button onClick={onClickBackHandler}>Atrás</button>
      </div>
    </div>
  );
};

export default Detail;
