import { useDispatch, useSelector } from "react-redux";
import CardsContainers from "../../components/Cardscontainer/CardsContainer";
import { useEffect, useState, useCallback } from "react";
import { getUsers, getNameUser } from "../../redux/actions";

const useDebouncedValue = (value, delay) => {
  const [debouncedValue, setDebouncedValue] = useState(value);

  useEffect(() => {
    const handler = setTimeout(() => {
      setDebouncedValue(value); // Actualiza el valor debounced después del delay
    }, delay);

    return () => {
      clearTimeout(handler); // Limpia el timeout cuando el componente se desmonta o el valor cambia
    };
  }, [value, delay]);

  return debouncedValue; // Retorna el valor debounced
};

const Home = () => {
  const dispatch = useDispatch();
  const users = useSelector((state) => state.users); // Obtiene la lista de usuarios del estado de Redux
  const loading = useSelector((state) => state.loading); // Obtiene el estado de carga de Redux
  const [input, setInput] = useState(""); // Estado local para la búsqueda por nombre

  // Valor debounced del input, se actualiza cada 300ms después de que el usuario deja de escribir
  const debouncedInput = useDebouncedValue(input, 300);

  // Efecto para cargar todos los usuarios al montar el componente
  useEffect(() => {
    dispatch(getUsers()); // Carga inicial de todos los usuarios
  }, [dispatch]);

  // Efecto para buscar usuarios por nombre cuando cambia el valor debouncedInput
  useEffect(() => {
    if (debouncedInput.trim()) {
      dispatch(getNameUser(debouncedInput)); // Si hay input, busca usuarios por nombre
    } else {
      dispatch(getUsers()); // Si no hay input, carga todos los usuarios
    }
  }, [debouncedInput, dispatch]);

  // Función manejadora para el cambio en el campo de búsqueda
  // Envuelta en useCallback para prevenir renders innecesarios
  const searchChange = useCallback((event) => {
    setInput(event.target.value); // Actualiza el input de búsqueda
  }, []);

  return (
    <div className="container">
      {/* Barra de navegación con el campo de búsqueda */}
      <nav className="navbar bg-body-tertiary">
        <div className="container-fluid">
          <form className="d-flex w-100" role="search">
            <input
              className="form-control me-2"
              type="search"
              placeholder="Search By Name User"
              aria-label="Search"
              value={input}
              onChange={searchChange} // Manejador de cambios en el input
            />
          </form>
        </div>
      </nav>

      {/* Renderizado condicional según el estado de carga y resultados */}
      {loading ? (
        <p>Loading...</p> // Si está cargando, muestra un mensaje
      ) : users.length === 0 ? (
        <p>No users found</p> // Si no hay usuarios, muestra un mensaje
      ) : (
        <CardsContainers /> // Si hay usuarios, renderiza el componente de tarjetas
      )}
    </div>
  );
};

export default Home;
