import { useNavigate, useParams } from "react-router-dom";
import axios from "axios";
import { useEffect, useState } from "react";
import { refreshProfileUser } from "../redux/actions";

const Detail = () => {
  const { id } = useParams();
  const [user, setUser] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    const getDetailUser = async () => {
      const detailUser = await axios.get(`http://localhost:3001/users/${id}`);
      setUser(detailUser.data);
    };
    getDetailUser();
    refreshProfile();
  }, []);

  const deleteHandler = async () => {
    const deleteUser = window.confirm(
      "Estas seguro de que quieres eliminar este usurio?"
    );
    if (deleteUser) {
      await axios.delete(`http://localhost:3001/users/deleteUser/${id}`);
      alert("usuario eliminado");
      navigate("/home");
    }
  };

  const refreshProfile = async () => {
    try {
      await dispatch(refreshProfileUser(userId)); // Esto recargará el perfil del usuario con los posts actualizados
    } catch (error) {
      console.error("Error al recargar el perfil:", error);
    }
  };

  const onclickDeleteHandler = async (id) => {
    const confirmDelete = window.confirm(
      "¿Estás seguro de que deseas eliminar este post?"
    );

    if (confirmDelete) {
      try {
        await axios.delete(`http://localhost:3001/postUser/deletePost/${id}`);
        alert("Posteo eliminado correctamente");
      } catch (error) {
        alert("Error al eliminar el post: " + error.message);
      }
    }
  };

  const onClickBackHandler = () => {
    navigate("/home");
  };

  return (
    <div>
      {user && (
        <>
          <h1>Name: {user.name}</h1>
          <h2>LastName:{user.lastName}</h2>
          <img src={`${user.image}}`} />
          <p>Email: {user.email}</p>
          <p>Phone: {user.phone}</p>
          <p>NameUser:{user.nameUser}</p>
          <p>Password:{user.password}</p>
          <ul>
            {user.Posts && user.Posts.length > 0 ? (
              user.Posts.map((post) => (
                <li key={post.id}>
                  <p>Comentario: {post.comment}</p>
                  <button onClick={() => onclickDeleteHandler(post.id)}>
                    Delete Post
                  </button>
                </li>
              ))
            ) : (
              <p>No hay posts disponibles.</p>
            )}
          </ul>
        </>
      )}
      <button onClick={deleteHandler}>Delete User</button>

      <button onClick={onClickBackHandler}>Back</button>
    </div>
  );
};

export default Detail;
