import { useDispatch, useSelector } from "react-redux";
import CardsContainers from "../components/Cardscontainer/CardsContainer";
import { useEffect, useState } from "react";
import { getUsers, getNameUser } from "../redux/actions";

const Home = () => {
  const dispatch = useDispatch();
  const loading = useSelector((state) => state.loading);
  const [input, setInput] = useState("");
  const [debouncedInput, setDebouncedInput] = useState(input);

  // Obtener todos los usuarios al cargar la página
  useEffect(() => {
    dispatch(getUsers());
  }, [dispatch]);

  // Debounce: Actualizar el valor del input con un retraso
  useEffect(() => {
    const handler = setTimeout(() => {
      setDebouncedInput(input);
    }, 300); // 300ms de retraso

    return () => {
      clearTimeout(handler); // Limpiar el timeout anterior si el usuario sigue escribiendo
    };
  }, [input]);

  // Efecto para despachar la búsqueda o todos los usuarios cuando el valor debounced cambie
  useEffect(() => {
    if (debouncedInput) {
      dispatch(getNameUser(debouncedInput)); // Realizar búsqueda si hay input
    } else {
      dispatch(getUsers()); // Mostrar todos los usuarios si el input está vacío
    }
  }, [debouncedInput, dispatch]);

  // Handler para actualizar el valor del input
  const searchChange = (event) => {
    setInput(event.target.value);
  };

  return (
    <>
      <input
        type="text"
        placeholder="Search By Name"
        value={input}
        onChange={searchChange}
      />
      {loading === true ? (
        <>
          <p>loading ...</p>
        </>
      ) : (
        <CardsContainers />
      )}
    </>
  );
};

export default Home;
