import { Link } from "react-router-dom";
import { useSelector } from "react-redux";
import "bootstrap/dist/css/bootstrap.min.css"; // Importar Bootstrap CSS
import "bootstrap/dist/js/bootstrap.bundle.min.js"; // Importar Bootstrap JS

const NavBar = () => {
  // Obtener el estado de autenticación y el perfil del usuario desde Redux
  const { isAuthenticated, profileUser } = useSelector((state) => ({
    isAuthenticated: state.isAuthenticated,
    profileUser: state.profileUser,
  }));

  return (
    <div id="containerNavBar" className="container-fluid">
      {/* Verificar si el usuario está autenticado y es administrador para asi mostrar el NavBar*/}
      {isAuthenticated && profileUser.admin && (
        <>
          <div className="card text-center">
            <div className="card-header">
              <ul className="nav nav-tabs card-header-tabs">
                <li className="nav-item">
                  {/* Enlace a la página de inicio */}
                  <Link className="nav-link" to="/home">
                    Home
                  </Link>
                </li>
                <li className="nav-item">
                  {/* Enlace a la página de perfil de usuario */}
                  <Link className="nav-link" to="/userProfile">
                    UserProfile
                  </Link>
                </li>
              </ul>
            </div>
          </div>
        </>
      )}
    </div>
  );
};

export default NavBar;
