import axios from "axios";
import { useState, useEffect } from "react";
import { useNavigate, useParams } from "react-router-dom";
import "./EditPost.css";

const EditPost = () => {
  const { userId, postId } = useParams();
  const [input, setInput] = useState({ id: postId, comment: "" });
  const navigate = useNavigate();

  // Obtener los datos del post cuando el componente se monta
  useEffect(() => {
    const getPost = async () => {
      try {
        const response = await axios.get(
          `http://localhost:3001/postUser/${postId}`
        );
        setInput({ id: postId, comment: response.data.comment }); // Actualiza el estado con el comentario actual
      } catch (error) {
        console.error("Error al obtener el post:", error);
      }
    };
    getPost();
  }, [postId]);

  const onChangeInputHandler = (e) => {
    const postComment = e.target.value;
    setInput((prevState) => ({ ...prevState, comment: postComment }));
  };

  const onSubmitHandler = async (e) => {
    e.preventDefault();
    await axios.put(`http://localhost:3001/postUser/modifyPost`, input);
    navigate(`/posts/${userId}`);
  };

  const onclickHandler = () => {
    navigate(`/posts/${userId}`);
  };

  return (
    <div className="container">
      <div className="editPostsContainer">
        <form onSubmit={onSubmitHandler}>
          <h3>Modificar comentario del post:</h3>
          <input
            type="text"
            value={input.comment}
            onChange={onChangeInputHandler}
          />
          <button>Enviar</button>
        </form>
        <div>
          <button onClick={onclickHandler}>Back</button>
        </div>
      </div>
    </div>
  );
};

export default EditPost;
