import { useState, useEffect } from "react";
import "./Login.css";
import { getUserProfile } from "../../redux/actions";
import { useDispatch } from "react-redux";
import { useNavigate } from "react-router-dom";
import { GoogleLogin } from "react-google-login";
import { gapi } from "gapi-script";
import axios from "axios";

const Login = () => {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const [login, setLogin] = useState({ nameUser: "", password: "" });

  const [form, setForm] = useState({
    name: "",
    lastName: "",
    image: "",
    email: "",
    phone: "",
    nameUser: "",
    password: "",
  });

  const [errors, setErrors] = useState({
    email: "",
  });

  // Inicializar gapi autenticacion con google
  useEffect(() => {
    const initClient = () => {
      gapi.client.init({
        clientId:
          "133661170359-fkebjnpt8sudfqujbcjbjrtj1cc8veoc.apps.googleusercontent.com",
      });
    };
    gapi.load("client:auth2", initClient);
  }, []);

  // Validación de correo electrónico
  const validate = (form) => {
    if (/^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,4})+$/.test(form.email)) {
      setErrors({ ...errors, email: "" });
    } else {
      setErrors({ ...errors, email: "Email incorrecto" });
    }
    if (form.email === "") setErrors({ ...errors, email: "Campo vacío" });
  };
  // Manejar cambios en los inputs
  const onChangeHandlerForm = (event) => {
    const property = event.target.name;
    const value = event.target.value;
    validate({ ...form, [property]: value });
    setForm({ ...form, [property]: value });
  };
  // Subir imagen a Cloudinary
  const onImageUploadHandler = async (event) => {
    const file = event.target.files[0];
    const formData = new FormData();
    formData.append("file", file);
    formData.append("upload_preset", "ucyfuf2e");

    try {
      const response = await axios.post(
        "https://api.cloudinary.com/v1_1/dekk238ao/image/upload",
        formData
      );
      setForm({ ...form, image: response.data.secure_url });
    } catch (error) {
      console.error("Error al subir la imagen:", error);
      alert("Error al subir la imagen");
    }
  };

  //genera contraseña aleatoria
  const generateRandomString = (length) => {
    const characters =
      "ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789";
    let result = "";
    for (let i = 0; i < length; i++) {
      result += characters.charAt(
        Math.floor(Math.random() * characters.length)
      );
    }
    return result;
  };

  // Manejar envío del formulario
  const submitHandler = (event) => {
    event.preventDefault();
    if (!errors.email) {
      try {
        axios
          .post("http://localhost:3001/users/createUser", form)
          .then((res) => alert("Usuario creado"))
          .catch((err) => {
            const errorMessage =
              err.response?.data?.error || "Error en la creación del usuario";
            alert(errorMessage);
          }); // Muestra el mensaje en la alerta
      } catch (error) {
        alert(
          "Error al crear el usuario:",
          error.response?.data || error.message
        );
      }
    }
  };

  const onSuccessGoogleForm = async (response) => {
    const userInfo = response.profileObj; // Información del perfil de Google

    // Generar valores aleatorios
    const randomPhone = `+1${
      Math.floor(Math.random() * 9000000000) + 1000000000
    }`; // Generar número de teléfono aleatorio
    const randomNameUser = `${userInfo.givenName}${
      userInfo.familyName
    }${Math.floor(Math.random() * 1000)}`; // Generar nombre de usuario aleatorio
    const randomPassword = generateRandomString(12); // Generar contraseña aleatoria de 12 caracteres

    setForm({
      ...form,
      name: userInfo.givenName,
      lastName: userInfo.familyName,
      email: userInfo.email,
      image: userInfo.imageUrl,
      phone: randomPhone,
      nameUser: randomNameUser,
      password: randomPassword,
    });

    // Enviar los datos al servidor
    try {
      const res = await axios.post("http://localhost:3001/users/createUser", {
        name: userInfo.givenName,
        lastName: userInfo.familyName,
        email: userInfo.email,
        image: userInfo.imageUrl,
        phone: randomPhone,
        nameUser: randomNameUser,
        password: randomPassword,
      });

      // Redirigir al perfil del usuario
      alert("Usuario creado con Google: " + res.data.message);
      const authenticated = await dispatch(
        getUserProfile({ nameUser: randomNameUser, password: randomPassword })
      );
      if (authenticated) {
        navigate("/userProfile");
      }
    } catch (error) {
      alert("Usuario ya existente, por favor modifique los datos");
    }
  };
  //aqui comienza el login
  // Inicializar gapi autenticación con Google
  useEffect(() => {
    const initClient = () => {
      gapi.client.init({
        clientId:
          "133661170359-fkebjnpt8sudfqujbcjbjrtj1cc8veoc.apps.googleusercontent.com", // Reemplaza por tu clientId
        scope: "email",
      });
    };
    gapi.load("client:auth2", initClient);
  }, []);

  // Código para cargar las animaciones del script.js
  useEffect(() => {
    const container = document.getElementById("container");
    const registerBtn = document.getElementById("register");
    const loginBtn = document.getElementById("login");

    registerBtn.addEventListener("click", () => {
      container.classList.add("active");
    });

    loginBtn.addEventListener("click", () => {
      container.classList.remove("active");
    });

    // Cleanup de los event listeners al desmontar el componente
    return () => {
      registerBtn.removeEventListener("click", () =>
        container.classList.add("active")
      );
      loginBtn.removeEventListener("click", () =>
        container.classList.remove("active")
      );
    };
  }, []);

  const onChangeHandler = (event) => {
    const property = event.target.name;
    const value = event.target.value;
    setLogin({ ...login, [property]: value });
  };

  const onSubmitHandler = async (event) => {
    try {
      event.preventDefault();
      const authenticated = await dispatch(getUserProfile(login));
      if (authenticated) {
        navigate("/userProfile");
      }
    } catch (error) {
      alert(error);
    }
  };

  const onSuccessGoogle = async (response) => {
    const userInfo = response.profileObj;

    try {
      const authenticated = await dispatch(
        getUserProfile({ email: userInfo.email })
      );
      if (authenticated) {
        navigate("/userProfile");
      } else {
        alert("Error en el inicio de sesión");
      }
    } catch (error) {
      console.error("Error al iniciar sesión con Google:", error);
      alert("Error en el inicio de sesión");
    }
  };

  return (
    <div>
      <div class="container" id="container">
        <div class="form-container sign-up">
          <form onSubmit={submitHandler}>
            <div>
              <GoogleLogin
                clientId="133661170359-fkebjnpt8sudfqujbcjbjrtj1cc8veoc.apps.googleusercontent.com"
                buttonText="Login with Google"
                onSuccess={onSuccessGoogleForm}
                cookiePolicy={"single_host_origin"}
              />
              <input
                type="text"
                value={form.name}
                onChange={onChangeHandlerForm}
                name="name"
                placeholder="name"
              />
              <input
                type="text"
                value={form.lastName}
                onChange={onChangeHandlerForm}
                name="lastName"
                placeholder="LastName"
              />
              <input
                type="file"
                onChange={onImageUploadHandler}
                name="image"
                placeholder="Image"
              />
              {form.image && (
                <img src={form.image} alt="Imagen subida" width="100" />
              )}
              <input
                type="text"
                value={form.email}
                onChange={onChangeHandlerForm}
                name="email"
                placeholder="Email"
              />
              {errors.email && <span>{errors.email}</span>}
              <input
                type="text"
                value={form.phone}
                onChange={onChangeHandlerForm}
                name="phone"
                placeholder="Phone"
              />
              <input
                type="text"
                value={form.nameUser}
                onChange={onChangeHandlerForm}
                name="nameUser"
                placeholder="NameUser"
              />
              <input
                type="password"
                value={form.password}
                onChange={onChangeHandlerForm}
                name="password"
                placeholder="Password"
              />
              <button type="submit">Enviar</button>
            </div>
          </form>
        </div>

        <div class="form-container sign-in">
          <form onSubmit={onSubmitHandler}>
            <h1>Sign In</h1>
            <div>
              <p>O inicia sesión con Google:</p>
              <GoogleLogin
                clientId="133661170359-fkebjnpt8sudfqujbcjbjrtj1cc8veoc.apps.googleusercontent.com" // Reemplaza con tu ID de cliente
                buttonText="Login with Google"
                onSuccess={onSuccessGoogle}
                cookiePolicy={"single_host_origin"}
              />
            </div>
            <span>or Name User</span>
            <input
              type="text"
              value={login.nameUser}
              name="nameUser"
              onChange={onChangeHandler}
              placeholder="NameUser"
            />
            <label>Password</label>
            <input
              type="password"
              value={login.password}
              name="password"
              onChange={onChangeHandler}
              placeholder="Password"
            />
            <button type="submit">Login</button>
          </form>
        </div>

        <div class="toggle-container">
          <div class="toggle">
            <div class="toggle-panel toggle-left">
              <h1>Welcome Back!</h1>
              <p>Enter your personal details to use all of site features</p>
              <button class="hidden" id="login">
                Sign In
              </button>
            </div>
            <div class="toggle-panel toggle-right">
              <h1>Hello, Friend!</h1>
              <p>
                Register with your personal details to use all of site features
              </p>
              <button class="hidden" id="register">
                Sign Up
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

