import axios from "axios";
import { useDispatch, useSelector } from "react-redux";
import { useNavigate, useParams } from "react-router-dom";
import { refreshProfileUser } from "../../redux/actions";
import { useEffect } from "react";

const GetPosts = () => {
  const navigate = useNavigate();
  const { userId } = useParams();
  const dispatch = useDispatch();
  const user = useSelector((state) => state.profileUser);

  // Recargar los datos del perfil
  const refreshProfile = async () => {
    try {
      await dispatch(refreshProfileUser(userId)); // Esto recargará el perfil del usuario con los posts actualizados
    } catch (error) {
      console.error("Error al recargar el perfil:", error);
    }
  };
  useEffect(() => {
    // Recargar el perfil del usuario cuando el componente se monte o el userId cambie
    refreshProfile();
  }, [userId]);

  const editCommentHandler = async (postId) => {
    navigate(`/editPosts/${userId}/${postId}`);
    await refreshProfile(); // Recargar el perfil después de editar
  };

  const onClickNewPostHandler = async () => {
    navigate(`/createPost/${userId}`);
    await refreshProfile(); // Recargar el perfil después de crear
  };

  const onclickDeleteHandler = async (id) => {
    const confirmDelete = window.confirm(
      "¿Estás seguro de que deseas eliminar este post?"
    );

    if (confirmDelete) {
      try {
        await axios.delete(`http://localhost:3001/postUser/deletePost/${id}`);
        alert("Posteo eliminado correctamente");
        await refreshProfile();
      } catch (error) {
        alert("Error al eliminar el post: " + error.message);
      }
    }
  };

  const onClickBackHandler = () => {
    navigate("/userProfile");
  };

  return (
    <>
      <li>
        {user.Posts && user.Posts.length > 0 ? (
          user.Posts.map((us) => {
            return (
              <li key={us.id}>
                <h1>{us.comment}</h1>;
                <button onClick={() => editCommentHandler(us.id)}>
                  Edit Comment
                </button>
                <button onClick={() => onclickDeleteHandler(us.id)}>
                  Delete Post
                </button>
              </li>
            );
          })
        ) : (
          <p>Cargando...</p>
        )}
        <button onClick={onClickNewPostHandler}>Crear un nuevo Post</button>
        <button onClick={onClickBackHandler}>Back</button>
      </li>
    </>
  );
};

export default GetPosts;
