import { GET_USERS } from "./actions";

const initialState = {
  users: [],
};
const rootReducer = (state = initialState, actions) => {
  switch (actions.type) {
    case GET_USERS:
      return { ...state, users: actions.payload };
    default:
      return { ...state };
  }
};
export default rootReducer;
