import { useState, useEffect } from "react";
import "./Login.css";
import { getUserProfile } from "../../redux/actions";
import { useDispatch, useSelector } from "react-redux";
import { useNavigate } from "react-router-dom";
import { GoogleLogin } from "react-google-login";
import { gapi } from "gapi-script";
import axios from "axios";

const Login = () => {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const [login, setLogin] = useState({ nameUser: "", password: "" });
  const autenticacion = useSelector((state) => state.isAuthenticated);

  // Estado para el formulario de registro
  const [form, setForm] = useState({
    name: "",
    lastName: "",
    image: "",
    email: "",
    phone: "",
    nameUser: "",
    password: "",
  });

  // Estado para los errores de validación
  const [errors, setErrors] = useState({ email: "" });

  // Efecto que navega al perfil del usuario si está autenticado
  useEffect(() => {
    if (autenticacion) navigate("/userProfile");
  }, [autenticacion, navigate]); // Dependencia de autenticacion y navigate

  // Inicializar la autenticación de Google
  useEffect(() => {
    const initClient = () => {
      gapi.client.init({
        clientId: "YOUR_CLIENT_ID.apps.googleusercontent.com",
        scope: "email",
      });
    };
    gapi.load("client:auth2", initClient);
  }, []);

  // Validación de correo electrónico
  const validateEmail = (email) => {
    if (!email) return "Campo vacío";
    if (!/^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,4})+$/.test(email)) {
      return "Email incorrecto";
    }
    return "";
  };

  // Manejar cambios en los inputs
  const onChangeHandlerForm = (event) => {
    const { name, value } = event.target;
    const emailError = validateEmail(value);
    setErrors({ ...errors, email: emailError });
    setForm({ ...form, [name]: value });
  };

  // Subir imagen a Cloudinary
  const onImageUploadHandler = async (event) => {
    const file = event.target.files[0];
    const formData = new FormData();
    formData.append("file", file);
    formData.append("upload_preset", "YOUR_UPLOAD_PRESET");

    try {
      const response = await axios.post(
        "https://api.cloudinary.com/v1_1/YOUR_CLOUD_NAME/image/upload",
        formData
      );
      setForm({ ...form, image: response.data.secure_url });
    } catch (error) {
      console.error("Error al subir la imagen:", error);
      alert("Error al subir la imagen");
    }
  };

  // Generar contraseña aleatoria
  const generateRandomString = (length) => {
    const characters =
      "ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789";
    return Array.from({ length }, () =>
      characters.charAt(Math.floor(Math.random() * characters.length))
    ).join("");
  };

  // Manejar envío del formulario
  const submitHandler = async (event) => {
    event.preventDefault();
    if (!errors.email) {
      try {
        await axios.post("http://localhost:3001/users/createUser", form);
        alert("Usuario creado");
      } catch (err) {
        const errorMessage =
          err.response?.data?.error || "Error en la creación del usuario";
        alert(errorMessage);
      }
    }
  };

  const onSuccessGoogleForm = async (response) => {
    const { givenName, familyName, email, imageUrl } = response.profileObj;

    // Generar valores aleatorios
    const randomPhone = `+1${
      Math.floor(Math.random() * 9000000000) + 1000000000
    }`;
    const randomNameUser = `${givenName}${familyName}${Math.floor(
      Math.random() * 1000
    )}`;
    const randomPassword = generateRandomString(12);

    // Actualizar el formulario con los datos de Google
    setForm({
      ...form,
      name: givenName,
      lastName: familyName,
      email: email,
      image: imageUrl,
      phone: randomPhone,
      nameUser: randomNameUser,
      password: randomPassword,
    });

    // Enviar los datos al servidor
    try {
      const res = await axios.post("http://localhost:3001/users/createUser", {
        name: givenName,
        lastName: familyName,
        email: email,
        image: imageUrl,
        phone: randomPhone,
        nameUser: randomNameUser,
        password: randomPassword,
      });

      alert("Usuario creado con Google: " + res.data.message);
      const authenticated = await dispatch(
        getUserProfile({ nameUser: randomNameUser, password: randomPassword })
      );
      if (authenticated) {
        navigate("/userProfile");
      }
    } catch (error) {
      alert("Usuario ya existente, por favor modifique los datos");
    }
  };

  // Inicializar la autenticación de Google y cargar el script para manejar el registro/inicio de sesión
  useEffect(() => {
    const container = document.getElementById("container");
    const registerBtn = document.getElementById("register");
    const loginBtn = document.getElementById("login");

    const handleRegisterClick = () => container.classList.add("active");
    const handleLoginClick = () => container.classList.remove("active");

    registerBtn.addEventListener("click", handleRegisterClick);
    loginBtn.addEventListener("click", handleLoginClick);

    // Cleanup de los event listeners al desmontar el componente
    return () => {
      registerBtn.removeEventListener("click", handleRegisterClick);
      loginBtn.removeEventListener("click", handleLoginClick);
    };
  }, []);

  const onChangeHandler = (event) => {
    const { name, value } = event.target;
    setLogin({ ...login, [name]: value });
  };

  const onSubmitHandler = async (event) => {
    event.preventDefault();
    try {
      const authenticated = await dispatch(getUserProfile(login));
      if (authenticated) {
        navigate("/userProfile");
      }
    } catch (error) {
      alert(error);
    }
  };

  const onSuccessGoogle = async (response) => {
    const { email } = response.profileObj;

    try {
      const authenticated = await dispatch(getUserProfile({ email }));
      if (authenticated) {
        navigate("/userProfile");
      } else {
        alert("Error en el inicio de sesión");
      }
    } catch (error) {
      console.error("Error al iniciar sesión con Google:", error);
      alert("Error en el inicio de sesión");
    }
  };

  return (
    <div>
      <div className="container" id="container">
        <div className="form-container sign-up">
          <form onSubmit={submitHandler}>
            <div>
              <h3>Create Account</h3>
              <input
                type="text"
                value={form.name}
                onChange={onChangeHandlerForm}
                name="name"
                placeholder="Name"
              />
              <input
                type="text"
                value={form.lastName}
                onChange={onChangeHandlerForm}
                name="lastName"
                placeholder="Last Name"
              />
              <input
                type="file"
                onChange={onImageUploadHandler}
                name="image"
                placeholder="Image"
              />
              {form.image && (
                <img src={form.image} alt="Uploaded" width="100" />
              )}
              <input
                type="text"
                value={form.email}
                onChange={onChangeHandlerForm}
                name="email"
                placeholder="Email"
              />
              {errors.email && <span>{errors.email}</span>}
              <input
                type="text"
                value={form.phone}
                onChange={onChangeHandlerForm}
                name="phone"
                placeholder="Phone"
              />
              <input
                type="text"
                value={form.nameUser}
                onChange={onChangeHandlerForm}
                name="nameUser"
                placeholder="Name User"
              />
              <input
                type="password"
                value={form.password}
                onChange={onChangeHandlerForm}
                name="password"
                placeholder="Password"
              />
              <button type="submit">Enviar</button>
              <p>Or sign in with Google:</p>
              <GoogleLogin
                clientId="YOUR_CLIENT_ID.apps.googleusercontent.com"
                buttonText="Login with Google"
                onSuccess={onSuccessGoogleForm}
                cookiePolicy={"single_host_origin"}
              />
            </div>
          </form>
        </div>

        <div className="form-container sign-in">
          <form onSubmit={onSubmitHandler}>
            <h1>Sign In</h1>
            <input
              type="text"
              value={login.nameUser}
              name="nameUser"
              onChange={onChangeHandler}
              placeholder="Name User"
            />
            <input
              type="password"
              value={login.password}
              name="password"
              onChange={onChangeHandler}
              placeholder="Password"
            />
            <button type="submit">Login</button>
            <p>Or sign in with Google:</p>
            <GoogleLogin
              clientId="YOUR_CLIENT_ID.apps.googleusercontent.com"
              buttonText="Login with Google"
              onSuccess={onSuccessGoogle}
              cookiePolicy={"single_host_origin"}
            />
          </form>
        </div>

        <div className="toggle-container">
          <div className="toggle">
