import { createStore, applyMiddleware, compose } from "redux";
import { thunk } from "redux-thunk"; // Middleware para manejar acciones asincrónicas
import rootReducer from "./reducer"; // Importar el reducer raíz

/**
 * Configuración de Redux Store
 * Crea una tienda Redux con middleware y soporte para Redux DevTools.
 */

// Verifica si Redux DevTools está disponible en el navegador y establece el enhancer correspondiente.
const composeEnhancer = window.__REDUX_DEVTOOLS_EXTENSION_COMPOSE__ || compose;

// Crear la tienda de Redux con el reducer raíz y aplicar el middleware thunk.
const store = createStore(
  rootReducer, // Reducer raíz que combina todos los reducers
  composeEnhancer(applyMiddleware(thunk)) // Aplicar middleware thunk y el enhancer
);

export default store; // Exportar la tienda para su uso en la aplicación
