import { useDispatch, useSelector } from "react-redux";
import { useNavigate } from "react-router-dom";
import { logOut } from "../../redux/actions";
import { useEffect, useState } from "react"; // Agregar useState para manejar la visibilidad
import { refreshProfileUser } from "../../redux/actions"; // Asegúrate de importar la acción para obtener los posts
import "./userProfile.css";

const UserProfile = () => {
  const profile = useSelector((state) => state.profileUser);
  const authenticated = useSelector((state) => state.isAuthenticated);
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const [showMore, setShowMore] = useState(false); // Estado para manejar la expansión de publicaciones

  // Redirigir al login si no está autenticado
  useEffect(() => {
    if (!authenticated) {
      navigate("/login");
    } else if (profile && profile.id) {
      dispatch(refreshProfileUser(profile.id)); // Llama a la acción para obtener los posts
    }
  }, [authenticated, navigate, profile, dispatch]);

  const onClickSingInHandler = () => {
    navigate("/form ");
  };
  const onClickLoginHandler = () => {
    navigate("/login");
  };
  const onClickLogOutHandler = () => {
    const closeSesion = window.confirm("¿Cerrar Sesión?");
    if (closeSesion) {
      dispatch(logOut());
      navigate("/login");
    }
  };
  const onClickModifyUserHandler = () => {
    navigate(`/modifyUser/${profile.id}`);
  };
  const allPostsHandler = () => {
    navigate(`/posts/${profile.id}`);
  };

  // Manejar la visualización de más publicaciones
  const handleShowMore = () => {
    setShowMore(!showMore);
  };

  return (
    <div className="container">
      {authenticated === true ? (
        profile ? (
          <div className="dateUser">
            <div className="dateUserHead">
              <div className="dateUserLeft">
                <h1>
                  {profile.name} {profile.lastName}
                </h1>
                <p>Email: {profile.email}</p>
                <p>Phone: {profile.phone}</p>
              </div>
              <div className="dateUserRight">
                <img src={`${profile.image}`} alt="Profile" />
              </div>
            </div>
            <div className="dateUserButton">
              <ul>
                {profile.Posts && profile.Posts.length > 0 ? (
                  profile.Posts.slice(
                    0,
                    showMore ? profile.Posts.length : 4
                  ).map((post) => (
                    <div>
                      <li key={post.id}>
                        <p>Comentario: {post.comment}</p>
                      </li>
                    </div>
                  ))
                ) : (
                  <p>No hay posts disponibles.</p>
                )}
              </ul>
              {profile.Posts && profile.Posts.length > 4 && (
                <div>
                  <button className="buttonNew" onClick={handleShowMore}>
                    {showMore ? "Mostrar menos" : "Mostrar más"}
                  </button>
                </div>
              )}
              <button onClick={allPostsHandler}>All Posts</button>
            </div>
            <div className="dateUserFoot">
              <button onClick={onClickModifyUserHandler}>Edit Profile</button>
              <button onClick={onClickLogOutHandler}>Log Out</button>
            </div>
          </div>
        ) : (
          <p>Cargando perfil...</p>
        )
      ) : (
        <>
          <p>
            No estás autenticado. Por favor, inicia sesión para ver el perfil.
          </p>
          <button onClick={onClickSingInHandler}>Sing In</button>
          <button onClick={onClickLoginHandler}>Login</button>
        </>
      )}
    </div>
  );
};

export default UserProfile;
