import { useState, useEffect } from "react";
import style from "./Login.module.css";
import { getUserProfile } from "../../redux/actions";
import { useDispatch } from "react-redux";
import { useNavigate } from "react-router-dom";
import { GoogleLogin } from "react-google-login";
import { gapi } from "gapi-script";
import axios from "axios";

const Login = () => {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const [login, setLogin] = useState({ nameUser: "", password: "" });

  // Inicializar gapi autenticación con Google
  useEffect(() => {
    const initClient = () => {
      gapi.client.init({
        clientId:
          "133661170359-fkebjnpt8sudfqujbcjbjrtj1cc8veoc.apps.googleusercontent.com", // Reemplaza por tu clientId
        scope: "email",
      });
    };
    gapi.load("client:auth2", initClient);
  }, []);

  const onChangeHandler = (event) => {
    const property = event.target.name;
    const value = event.target.value;
    setLogin({ ...login, [property]: value });
  };

  const onSubmitHandler = async (event) => {
    try {
      event.preventDefault();
      const authenticated = await dispatch(getUserProfile(login));
      if (authenticated) {
        navigate("/userProfile");
      }
    } catch (error) {
      alert(error);
    }
  };

  const onSuccessGoogle = async (response) => {
    const userInfo = response.profileObj;

    // Enviar los datos del usuario a tu backend para autenticación
    try {
      const res = await axios.post("http://localhost:3001/users/login", {
        email: userInfo.email,
      });

      if (res.data.success) {
        // Si el inicio de sesión es exitoso, redirigir al perfil del usuario
        navigate("/userProfile");
      } else {
        alert("Error en el inicio de sesión");
      }
    } catch (error) {
      console.error("Error al iniciar sesión con Google:", error);
      alert("Error en el inicio de sesión");
    }
  };

  return (
    <div>
      <form onSubmit={onSubmitHandler}>
        <div className={style.mainContainer}>
          <label>User</label>
          <input
            type="text"
            value={login.nameUser}
            name="nameUser"
            onChange={onChangeHandler}
          />
          <label>Password</label>
          <input
            type="password"
            value={login.password}
            name="password"
            onChange={onChangeHandler}
          />
          <button type="submit">Login</button>
        </div>
      </form>

      <div className={style.oauthContainer}>
        <p>O inicia sesión con Google:</p>
        <GoogleLogin
          clientId="133661170359-fkebjnpt8sudfqujbcjbjrtj1cc8veoc.apps.googleusercontent.com" // Reemplaza con tu ID de cliente
          buttonText="Login with Google"
          onSuccess={onSuccessGoogle}
          cookiePolicy={"single_host_origin"}
        />
      </div>
    </div>
  );
};

export default Login;
