import axios from "axios";
import { useDispatch, useSelector } from "react-redux";
import { useNavigate, useParams } from "react-router-dom";
import { refreshProfileUser } from "../../../redux/actions";
import { useEffect, useState } from "react";
import "./Posts.css";

const GetPostsUser = () => {
  const { userId } = useParams();
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const user = useSelector((state) => state.profileUser);
  const [posts, setPosts] = useState([]); // Cambiar a arreglo vacío inicialmente
  const [loading, setLoading] = useState(true); // Para controlar la carga de datos

  const allPostsAdmin = async () => {
    try {
      const allPosts = await axios.get("http://localhost:3001/postUser");
      setPosts(allPosts.data); // Asegúrate de obtener los datos correctos
    } catch (error) {
      console.error("Error al obtener los posts:", error);
    }
  };

  const refreshProfile = async () => {
    try {
      await dispatch(refreshProfileUser(userId));
    } catch (error) {
      console.error("Error al recargar el perfil:", error);
    }
  };

  const getUserData = async (postId) => {
    try {
      const response = await axios.get(
        `http://localhost:3001/postUser/relationUser/${postId}`
      );
      return response.data.User; // Asegúrate de que `User` sea la propiedad correcta
    } catch (error) {
      console.error(
        `Error al obtener datos del usuario para el post ${postId}:`,
        error.response.data
      );
      return null; // Retorna null en caso de error
    }
  };

  const fetchUserDataForPosts = async () => {
    setLoading(true); // Inicia la carga
    const userPromises = posts.map(async (post) => {
      if (post.id) {
        // Verifica que post.id tenga un valor
        const user = await getUserData(post.id);
        return { ...post, user }; // Añade los datos del usuario al post
      }
      return post; // Retorna el post sin usuario si post.id no es válido
    });

    const postsWithUsers = await Promise.all(userPromises);
    setPosts(postsWithUsers);
    setLoading(false); // Termina la carga
  };

  useEffect(() => {
    refreshProfile();
    allPostsAdmin();
  }, [userId]);

  useEffect(() => {
    if (posts.length > 0 && loading === false) {
      fetchUserDataForPosts();
    }
  }, [posts]); // No se debe incluir loading aquí

  const editCommentHandler = async (postId) => {
    navigate(`/editPosts/${userId}/${postId}`);
    await refreshProfile();
  };

  const onClickNewPostHandler = async () => {
    navigate(`/createPost/${userId}`);
    await refreshProfile();
  };

  const onclickDeleteHandler = async (id) => {
    const confirmDelete = window.confirm(
      "¿Estás seguro de que deseas eliminar este post?"
    );

    if (confirmDelete) {
      try {
        await axios.delete(`http://localhost:3001/postUser/deletePost/${id}`);
        alert("Posteo eliminado correctamente");
        await refreshProfile();
      } catch (error) {
        alert("Error al eliminar el post: " + error.message);
      }
    }
  };

  const onClickBackHandler = () => {
    navigate("/userProfile");
  };

  return (
    <div className="container">
      <h3>Posts User</h3>

      <div className="allPotsUser">
        {user.admin === true ? (
          loading ? (
            <p>Cargando posts...</p> // Mensaje de carga
          ) : posts.length > 0 ? (
            posts.map((post) => (
              <div className="itemPostUser" key={post.id}>
                <div>
                  <h4>{post.comment}</h4>
                  {post.user && (
                    <p>
                      Usuario: {post.user.name} {post.user.lastName}
                    </p> // Muestra los datos del usuario
                  )}
                </div>
                <div className="itemPostUserButton">
                  <button onClick={() => editCommentHandler(post.id)}>
                    Edit Comment
                  </button>
                  <button
                    onClick={() => onclickDeleteHandler(post.id)}
                    className="deletePost"
                  >
                    Delete Post
                  </button>
                </div>
              </div>
            ))
          ) : (
            <p>No hay posts disponibles</p>
          )
        ) : user.Posts && user.Posts.length > 0 ? (
          user.Posts.map((us) => (
            <div className="itemPostUser" key={us.id}>
              <div>
                <h4>{us.comment}</h4>
              </div>
              <div className="itemPostUserButton">
                <button onClick={() => editCommentHandler(us.id)}>
                  Edit Comment
                </button>
                <button
                  onClick={() => onclickDeleteHandler(us.id)}
                  className="deletePost"
                >
                  Delete Post
                </button>
              </div>
            </div>
          ))
        ) : (
          <p>Cargando...</p>
        )}
      </div>

      <div className="itemPostUserButton">
        <button onClick={onClickNewPostHandler}>Crear un nuevo Post</button>
        <button onClick={onClickBackHandler}>Back</button>
      </div>
    </div>
  );
};

export default GetPostsUser;
