import { useNavigate, useParams } from "react-router-dom";
import axios from "axios";
import { useEffect, useState } from "react";
import { refreshProfileUser, getUsers } from "../../redux/actions";
import { useDispatch } from "react-redux";
import "./Detail.css";

const Detail = () => {
  const { id } = useParams(); // Obtiene el ID del usuario de los parámetros de la URL
  const [user, setUser] = useState(""); // Estado local para almacenar los datos del usuario
  const navigate = useNavigate(); // Hook para redirigir a otras rutas
  const dispatch = useDispatch(); // Hook para obtener el dispatch de Redux

  // Efecto para obtener los detalles del usuario cuando se monta el componente
  useEffect(() => {
    const getDetailUser = async () => {
      const detailUser = await axios.get(`http://localhost:3001/users/${id}`); // Solicita los detalles del usuario por ID
      setUser(detailUser.data); // Actualiza el estado con los datos del usuario
    };
    getDetailUser(); // Llama a la función para obtener los detalles del usuario
  }, [id]);

  // Maneja la eliminación del usuario
  const deleteHandlerUser = async () => {
    const deleteUser = window.confirm(
      "¿Estás seguro de que deseas eliminar este usuario?" // Confirmación para eliminar el usuario
    );
    if (deleteUser) {
      try {
        await axios.delete(`http://localhost:3001/users/deleteUser/${id}`); // Realiza la solicitud para eliminar el usuario
        alert("Usuario eliminado"); // Alerta de éxito

        // Actualiza la lista de usuarios en Redux
        dispatch(getUsers());

        // Redirige al home
        navigate("/home");
      } catch (error) {
        alert("Error al eliminar el usuario: " + error.message); // Manejo de errores
      }
    }
  };

  // Función para refrescar el perfil del usuario
  const refreshProfile = async () => {
    try {
      await dispatch(refreshProfileUser(id)); // Recarga el perfil actualizado del usuario
    } catch (error) {
      console.error("Error al recargar el perfil:", error); // Manejo de errores
    }
  };

  // Maneja la eliminación de un post del usuario
  const onclickDeletePostHandler = async (postId) => {
    const confirmDelete = window.confirm(
      "¿Estás seguro de que deseas eliminar este post?" // Confirmación para eliminar el post
    );

    if (confirmDelete) {
      try {
        await axios.delete(
          `http://localhost:3001/postUser/deletePost/${postId}` // Realiza la solicitud para eliminar el post
        );
        alert("Post eliminado correctamente"); // Alerta de éxito

        // Filtra el post eliminado del estado local
        const updatedPosts = user.Posts.filter((post) => post.id !== postId);
        setUser({ ...user, Posts: updatedPosts }); // Actualiza el estado del usuario con los posts restantes
        dispatch(getUsers()); // Actualiza la lista de usuarios en Redux
        navigate("/home"); // Redirige al home
      } catch (error) {
        alert("Error al eliminar el post: " + error.message); // Manejo de errores
      }
    }
  };

  // Maneja la navegación de regreso al home
  const onClickBackHandler = () => {
    navigate("/home"); // Redirige al home
  };

  return (
    <div className="container">
      {user && ( // Verifica si hay datos del usuario
        <>
          <div className="headContainer">
            <h1>
              {user.name} {user.lastName}
            </h1>
            <img src={`${user.image}`} alt={`${user.name} ${user.lastName}`} />{" "}
          </div>
          <div className="dateUser">
            <p>
              <strong>Email: </strong>
              {user.email}
            </p>
            <p>
              <strong>Teléfono: </strong>
              {user.phone}
            </p>
            <p>
              <strong>Usuario:</strong> {user.nameUser}
            </p>
            <p>
              <strong>Contraseña:</strong> {user.password}
            </p>
          </div>
          <div className="postsUser">
            {user.Posts && user.Posts.length > 0 ? ( // Verifica si hay publicaciones del usuario
              user.Posts.map(
                (
                  post // Mapea cada publicación
                ) => (
                  <div className="cardsContainer" key={post.id}>
                    <p>{post.comment}</p>
                    <button
                      onClick={() => onclickDeletePostHandler(post.id)} // Maneja la eliminación del post
                      className="deletePost"
                    >
                      Delete post
                    </button>
                  </div>
                )
              )
            ) : (
              <p>No posts available.</p> // Mensaje si no hay publicaciones
            )}
          </div>
        </>
      )}
      <div className="buttonModifyUser">
        <button onClick={deleteHandlerUser} className="deleteUser"></button>

        <button onClick={onClickBackHandler} className="backButton"></button>
      </div>
    </div>
  );
};

export default Detail;
