import { useDispatch, useSelector } from "react-redux";
import CardsContainers from "../../components/Cardscontainer/CardsContainer";
import { useEffect, useState, useCallback } from "react";
import { getUsers, getNameUser } from "../../redux/actions";

const useDebouncedValue = (value, delay) => {
  const [debouncedValue, setDebouncedValue] = useState(value);

  useEffect(() => {
    const handler = setTimeout(() => {
      setDebouncedValue(value);
    }, delay);

    return () => {
      clearTimeout(handler);
    };
  }, [value, delay]);

  return debouncedValue;
};

const Home = () => {
  const dispatch = useDispatch();
  const users = useSelector((state) => state.users);
  const loading = useSelector((state) => state.loading);
  const [input, setInput] = useState("");

  const debouncedInput = useDebouncedValue(input, 300);

  useEffect(() => {
    dispatch(getUsers());
  }, [dispatch]);

  useEffect(() => {
    if (debouncedInput.trim()) {
      dispatch(getNameUser(debouncedInput));
    } else {
      dispatch(getUsers());
    }
  }, [debouncedInput, dispatch]);

  const searchChange = useCallback((event) => {
    setInput(event.target.value);
  }, []);

  return (
    <div className="container">
      <nav className="navbar bg-body-tertiary">
        <div className="container-fluid">
          <form className="d-flex w-100" role="search">
            <input
              className="form-control me-2"
              type="search"
              placeholder="Search By Name User"
              aria-label="Search"
              value={input}
              onChange={searchChange}
            />
          </form>
        </div>
      </nav>
      {loading ? (
        <p>Loading...</p>
      ) : users.length === 0 ? (
        <p>No users found</p>
      ) : (
        <CardsContainers />
      )}
    </div>
  );
};

export default Home;
