import { useState } from "react";
import style from "./Form.module.css";
import axios from "axios";

const Form = () => {
  const [form, setForm] = useState({
    name: "",
    lastName: "",
    image: "",
    email: "",
    phone: "",
    userName: "",
    password: "",
  });
  const onChangeHandler = (event) => {
    const property = event.target.name;
    const value = event.target.value;
    setForm({ ...form, [property]: value });
  };
  const submitHandler = () => {
    axios.get("http://localhost:3001/users/createUser", form);
    alert("Usuario creado");
    setForm("");
  };
  return (
    <div>
      <form onSubmit={submitHandler}>
        <div className={style.mainContainer}>
          <label>Name: </label>
          <input
            type="text"
            value={form}
            onChange={onChangeHandler}
            name="name"
          />
          <label>LastName: </label>
          <input
            type="text"
            value={form}
            onChange={onChangeHandler}
            name="lastName"
          />
          <label>Image: </label>
          <input
            type="text"
            value={form}
            onChange={onChangeHandler}
            name="image"
          />
          <label>Email: </label>
          <input
            type="text"
            value={form}
            onChange={onChangeHandler}
            name="email"
          />
          <label>Phone: </label>
          <input
            type="text"
            value={form}
            onChange={onChangeHandler}
            name="phone"
          />
          <label>NameUser: </label>
          <input
            type="text"
            value={form}
            onChange={onChangeHandler}
            name="userName"
          />
          <label>Password: </label>
          <input
            type="text"
            value={form}
            onChange={onChangeHandler}
            name="password"
          />
          <button type="submit">Enviar</button>
        </div>
      </form>
    </div>
  );
};

export default Form;
