import { useState } from "react";
import { useSelector } from "react-redux";
import Home from "../../views/Home/Home";
import UserProfile from "../userProfile/userProfile";
import "bootstrap/dist/css/bootstrap.min.css";
import "bootstrap/dist/js/bootstrap.bundle.min.js";

const NavBar = () => {
  const authenticated = useSelector((state) => state.isAuthenticated);
  const adminUser = useSelector((state) => state.profileUser);

  // Estado local para determinar qué componente mostrar
  const [activeComponent, setActiveComponent] = useState("home");

  const handleComponentChange = (component) => {
    setActiveComponent(component);
  };

  return (
    <div id="containerNavBar" className="container-fluid">
      {authenticated === true && adminUser.admin === true && (
        <div className="card text-center">
          <div className="card-header">
            <ul className="nav nav-tabs card-header-tabs">
              <li className="nav-item">
                <button
                  className="nav-link"
                  onClick={() => handleComponentChange("home")}
                >
                  Home
                </button>
              </li>
              <li className="nav-item">
                <button
                  className="nav-link"
                  onClick={() => handleComponentChange("userProfile")}
                >
                  UserProfile
                </button>
              </li>
            </ul>
          </div>
          <div className="card-body">
            {activeComponent === "home" ? <Home /> : <UserProfile />}
          </div>
        </div>
      )}
    </div>
  );
};

export default NavBar;
